#include "main.h"

#include <stdio.h>
#include <stdlib.h>

#include "gba.h"
#include "images/sprite.h"
#include "images/ticketmaster.h"
#include "images/lostgamescreen.h"
#include "images/taylorswiftsing.h"
#include "images/wingamescreen.h"
#include "images/kanyewest.h"
#include "images/snake.h"
#include "images/startscreen.h"
#include "images/blackbox.h"

